package es.bonus.android.data

import android.util.Base64
import es.bonus.android.features.*
import kotlinx.coroutines.runBlocking
import java.math.BigInteger
import java.util.*


object DummyBackend {
    private var GLOBAL_ID_COUNTER = BigInteger.ZERO
    fun nextId(): Id {
        val result = GLOBAL_ID_COUNTER
        GLOBAL_ID_COUNTER += BigInteger.ONE

        return result
    }

    val users = mutableMapOf<UserId, User>()
    val companies = mutableMapOf<CompanyId, Company>()
    val rewardImages = mutableMapOf<RewardImageId, RewardImage>()
    val rewards = mutableMapOf<RewardId, Reward>()
    val exchangeOffers = mutableMapOf<ExchangeOfferId, ExchangeOffer>()
    val events = mutableListOf<Event>()
}

class DummyClient : IClient {
    lateinit var currentUserId: UserId

    override suspend fun login(username: String, password: String): Result<User, String> {
        // validation
        val user = DummyBackend.users.values.find { it.username == username }
            ?: return Result.Failure("User '$username' not found!")

        // logic
        return Result.Success(user)
    }

    override suspend fun registerUser(
        username: String,
        password: String,
        avatarBytes: ByteArray
    ): Result<User, String> {
        // validation
        if (DummyBackend.users.values.find { it.username == username } != null)
            return Result.Failure("Username '$username' is already taken!")

        // logic
        val user = User(
            id = DummyBackend.nextId(),
            username = username,
            avatarBytes = avatarBytes,
            ownedCompaniesIds = emptyList(),
            bonuses = emptyMap(),
            rewards = emptyMap()
        )

        DummyBackend.users[user.id!!] = user

        return Result.Success(user)
    }

    override suspend fun registerCompany(
        name: String,
        logoBytes: ByteArray,
        description: String,
        discount: BigInteger
    ): Result<CompanyId, String> {
        // logic
        val company = Company(
            id = DummyBackend.nextId(),
            name = name,
            logoBytes = logoBytes,
            description = description,
            discount = discount,
            ownerId = currentUserId,
            rewardImagesIds = emptyList(),
            bonuses = emptyMap()
        )

        DummyBackend.companies[company.id!!] = company

        return Result.Success(company.id)
    }

    override suspend fun editCompany(
        id: CompanyId,
        name: String?,
        logoBytes: ByteArray?,
        description: String?,
        discount: BigInteger?
    ): Result<Unit, String> {
        // validation
        val company = DummyBackend.companies[id]
            ?: return Result.Failure("Company with id '$id' not found!")

        if (company.ownerId != currentUserId)
            return Result.Failure("User with id '$currentUserId' is not an owner of the company!")

        // logic
        DummyBackend.companies[id] = company.copy(
            name = name ?: company.name,
            logoBytes = logoBytes ?: company.logoBytes,
            description = description ?: company.description,
            discount = discount ?: company.discount
        )

        return Result.Success(Unit)
    }

    override suspend fun createRewardImage(
        rewardImage: RewardImage
    ): Result<RewardImageId, String> {
        // validation
        val companyId = when (rewardImage) {
            is RewardImage.Discount -> rewardImage.ofCompanyId
            is RewardImage.Gift -> rewardImage.ofCompanyId
        }

        val company = DummyBackend.companies[companyId]
            ?: return Result.Failure("Company with id '$companyId' not found!")

        if (company.ownerId != currentUserId)
            return Result.Failure("User with id '$currentUserId' is not an owner of the company!")

        // logic
        val rewardImageId = DummyBackend.nextId()

        val rewardImg = when (rewardImage) {
            is RewardImage.Discount -> rewardImage.copy(id = rewardImageId)
            is RewardImage.Gift -> rewardImage.copy(id = rewardImageId)
        }

        DummyBackend.companies[company.id!!] =
            company.copy(rewardImagesIds = company.rewardImagesIds + rewardImageId)
        DummyBackend.rewardImages[rewardImageId] = rewardImg

        return Result.Success(rewardImageId)
    }

    override suspend fun removeRewardImage(rewardImageId: RewardImageId): Result<Unit, String> {
        // validation
        val rewardImage = DummyBackend.rewardImages[rewardImageId]
            ?: return Result.Failure("Reward image with id '$rewardImageId' not found!")

        val companyId = when (rewardImage) {
            is RewardImage.Discount -> rewardImage.ofCompanyId
            is RewardImage.Gift -> rewardImage.ofCompanyId
        }

        val company = DummyBackend.companies[companyId]
            ?: return Result.Failure("Company with id '$companyId' not found!")

        if (company.ownerId != currentUserId)
            return Result.Failure("User with id '$currentUserId' is not an owner of the company!")

        // logic
        DummyBackend.rewardImages.remove(rewardImageId)

        return Result.Success(Unit)
    }

    override suspend fun issueBonuses(
        fromCompanyId: CompanyId,
        toUserId: UserId,
        amount: BigInteger
    ): Result<Unit, String> {
        // validation
        val company = DummyBackend.companies[fromCompanyId]
            ?: return Result.Failure("Company with id '$fromCompanyId' not found!")

        if (company.ownerId != currentUserId)
            return Result.Failure("User with id '$currentUserId' is not an owner of the company!")

        val user = DummyBackend.users[toUserId]
            ?: return Result.Failure("User with id '$toUserId' not found!")

        val companyBonuses = company.bonuses.toMutableMap()
        val companyUserBalance = companyBonuses[toUserId] ?: BigInteger.ZERO
        val userBonuses = user.bonuses.toMutableMap()
        val userBalance = userBonuses[fromCompanyId] ?: BigInteger.ZERO

        if (userBalance != companyUserBalance)
            return Result.Failure("Something went wrong with the balances. They should always be equal.")

        // logic
        companyBonuses[toUserId] = companyUserBalance + amount
        userBonuses[fromCompanyId] = userBalance + amount

        DummyBackend.companies[fromCompanyId] = company.copy(bonuses = companyBonuses)
        DummyBackend.users[toUserId] = user.copy(bonuses = userBonuses)

        val event = Event(
            type = EventType.COMPANY_BONUSES_ISSUED,
            companyId = fromCompanyId,
            userId = toUserId,
            ownedAsset = OwnedAsset.Bonus(amount.bonuses),
            timestamp = Date().time.toBigInteger()
        )
        DummyBackend.events.add(event)

        return Result.Success(Unit)
    }

    override suspend fun transferBonuses(
        ofCompanyId: CompanyId,
        toUserId: UserId,
        amount: BigInteger
    ): Result<Unit, String> {
        // validation
        val fromUser = DummyBackend.users[currentUserId]
            ?: return Result.Failure("User with id '$currentUserId' not found!")

        val toUser = DummyBackend.users[toUserId]
            ?: return Result.Failure("User with id '$toUserId' not found!")

        val company = DummyBackend.companies[ofCompanyId]
            ?: return Result.Failure("Company with id '$ofCompanyId' not found!")
        val companyBonuses = company.bonuses.toMutableMap()

        val fromUserBalance = fromUser.bonuses[ofCompanyId] ?: BigInteger.ZERO
        if (fromUserBalance < amount)
            return Result.Failure("User with id '$currentUserId' doesn't have enough bonuses to transfer!")

        val toUserBalance = toUser.bonuses[ofCompanyId] ?: BigInteger.ZERO

        val fromUserCompanyBalance = companyBonuses[currentUserId] ?: BigInteger.ZERO
        if (fromUserBalance != fromUserCompanyBalance)
            return Result.Failure("Something went wrong with the balances. They should always be equal.")

        val toUserCompanyBalance = companyBonuses[toUserId] ?: BigInteger.ZERO
        if (toUserBalance != toUserCompanyBalance)
            return Result.Failure("Something went wrong with the balances. They should always be equal.")

        // logic
        companyBonuses[currentUserId] = fromUserCompanyBalance - amount
        companyBonuses[toUserId] = toUserCompanyBalance + amount

        val fromUserBonuses = fromUser.bonuses.toMutableMap()
        fromUserBonuses[ofCompanyId] = fromUserBalance - amount

        val toUserBonuses = toUser.bonuses.toMutableMap()
        toUserBonuses[ofCompanyId] = toUserBalance + amount

        DummyBackend.users[currentUserId] = fromUser.copy(bonuses = fromUserBonuses)
        DummyBackend.users[toUserId] = toUser.copy(bonuses = toUserBonuses)
        DummyBackend.companies[ofCompanyId] = company.copy(bonuses = companyBonuses)

        return Result.Success(Unit)
    }

    override suspend fun purchaseCustomReward(
        rewardImageId: RewardImageId
    ): Result<RewardId, String> {
        // validation
        val rewardImage = DummyBackend.rewardImages[rewardImageId]
            ?: return Result.Failure("Reward image with id '$rewardImageId' not found!")

        val user = DummyBackend.users[currentUserId]
            ?: return Result.Failure("User with id '$currentUserId' not found!")

        val (companyId, price) = when (rewardImage) {
            is RewardImage.Discount -> Pair(rewardImage.ofCompanyId, rewardImage.price)
            is RewardImage.Gift -> Pair(rewardImage.ofCompanyId, rewardImage.price)
        }

        val company = DummyBackend.companies[companyId]
            ?: return Result.Failure("Company with id '$companyId' not found!")

        val userBonuses = user.bonuses.toMutableMap()
        val companyBonuses = company.bonuses.toMutableMap()

        val userBalance = userBonuses[companyId] ?: BigInteger.ZERO
        val companyUserBalance = companyBonuses[currentUserId] ?: BigInteger.ZERO

        if (userBalance != companyUserBalance)
            return Result.Failure("Something went wrong with the balances. They should always be equal.")

        if (userBalance < price)
            return Result.Failure("User doesn't have enough bonuses for the reward purchase!")

        // logic
        userBonuses[companyId] = userBalance - price
        companyBonuses[currentUserId] = companyUserBalance - price

        val reward = Reward.Custom(
            id = DummyBackend.nextId(),
            imageId = rewardImageId,
            ownerId = currentUserId
        )

        val userRewards = user.rewards.toMutableMap()
        val userRewardsList = (user.rewards[companyId] ?: emptyList()) + reward.id
        userRewards[companyId] = userRewardsList

        DummyBackend.users[currentUserId] = user.copy(
            bonuses = userBonuses,
            rewards = userRewards
        )
        DummyBackend.companies[companyId] = company.copy(bonuses = companyBonuses)
        DummyBackend.rewards[reward.id] = reward

        return Result.Success(reward.id)
    }

    override suspend fun purchaseCommonReward(
        companyId: CompanyId,
        amount: BigInteger
    ): Result<RewardId, String> {
        // validation
        val user = DummyBackend.users[currentUserId]
            ?: return Result.Failure("User with id '$currentUserId' not found!")

        val company = DummyBackend.companies[companyId]
            ?: return Result.Failure("Company with id '$companyId' not found!")

        val userBonuses = user.bonuses.toMutableMap()
        val companyBonuses = company.bonuses.toMutableMap()

        val userBalance = userBonuses[companyId] ?: BigInteger.ZERO
        val companyUserBalance = companyBonuses[currentUserId] ?: BigInteger.ZERO

        if (userBalance != companyUserBalance)
            return Result.Failure("Something went wrong with the balances. They should always be equal.")

        val price = amount * company.discount

        if (userBalance < price)
            return Result.Failure("User doesn't have enough bonuses for the reward purchase!")

        // logic
        userBonuses[companyId] = userBalance - price
        companyBonuses[currentUserId] = companyUserBalance - price

        val reward = Reward.Common(
            id = DummyBackend.nextId(),
            ownerId = currentUserId,
            amount = OwnedAsset.Discount.Currency(price.rub),
            ofCompanyId = companyId
        )

        val userRewards = user.rewards.toMutableMap()
        val userRewardsList = (user.rewards[companyId] ?: emptyList()) + reward.id
        userRewards[companyId] = userRewardsList

        DummyBackend.users[currentUserId] = user.copy(
            bonuses = userBonuses,
            rewards = userRewards
        )
        DummyBackend.companies[companyId] = company.copy(bonuses = companyBonuses)
        DummyBackend.rewards[reward.id] = reward

        return Result.Success(reward.id)
    }

    override suspend fun useReward(rewardId: RewardId): Result<Unit, String> {
        // validation
        val reward = DummyBackend.rewards[rewardId]
            ?: return Result.Failure("Reward with id '$rewardId' not found!")

        val (ownerId, used) = when (reward) {
            is Reward.Custom -> Pair(reward.ownerId, reward.used)
            is Reward.Common -> Pair(reward.ownerId, reward.used)
        }

        if (ownerId != currentUserId)
            return Result.Failure("User with id '$currentUserId' is not an owner of the reward with id '$rewardId'!")

        if (used)
            return Result.Failure("The reward with id '$rewardId' is already used!")

        when (reward) {
            is Reward.Custom -> DummyBackend.rewards[rewardId] = reward.copy(used = true)
            is Reward.Common -> DummyBackend.rewards[rewardId] = reward.copy(used = true)
        }

        return Result.Success(Unit)
    }

    override suspend fun acceptReward(rewardId: RewardId): Result<Unit, String> {
        // validation
        val reward = DummyBackend.rewards[rewardId]
            ?: return Result.Failure("Reward with id '$rewardId' not found!")

        val (companyId, used, ownerId) = when (reward) {
            is Reward.Custom -> {
                val img = DummyBackend.rewardImages[reward.imageId]
                    ?: return Result.Failure("Reward image with id '${reward.imageId} not found!")

                val companyId = when (img) {
                    is RewardImage.Discount -> img.ofCompanyId
                    is RewardImage.Gift -> img.ofCompanyId
                }

                Triple(companyId, reward.used, reward.ownerId)
            }
            is Reward.Common -> Triple(reward.ofCompanyId, reward.used, reward.ownerId)
        }

        val company = DummyBackend.companies[companyId]
            ?: return Result.Failure("Company with id '$companyId' not found!")

        if (company.ownerId != currentUserId)
            return Result.Failure("User with id '$currentUserId' is not an owner of the company!")

        if (!used)
            return Result.Failure("The reward is not used by an owner!")

        val ownerUser = DummyBackend.users[ownerId]
            ?: return Result.Failure("User with id '$ownerId' not found!")

        // logic
        val userRewards = ownerUser.rewards.toMutableMap()
        val userCompanyRewards = (userRewards[companyId] ?: emptyList()).toMutableList()
        userCompanyRewards.remove(rewardId)
        userRewards[companyId] = userCompanyRewards

        DummyBackend.rewards.remove(rewardId)
        DummyBackend.users[ownerId] = ownerUser.copy(rewards = userRewards)

        return Result.Success(Unit)
    }

    override suspend fun placeExchangeOffer(
        sellsCompanyId: CompanyId,
        sellsAmount: BigInteger,
        buysCompanyId: CompanyId,
        buysAmount: BigInteger
    ): Result<ExchangeOfferId, String> {
        // validation
        val sellsCompany = DummyBackend.companies[sellsCompanyId]
            ?: return Result.Failure("Company with id '$sellsCompanyId' not found!")

        DummyBackend.companies[buysCompanyId]
            ?: return Result.Failure("Company with id '$buysCompanyId' not found!")

        val user = DummyBackend.users[currentUserId]
            ?: return Result.Failure("User with id '$currentUserId' not found!")

        val userBonuses = user.bonuses.toMutableMap()
        val companyBonuses = sellsCompany.bonuses.toMutableMap()

        val userBalance = userBonuses[sellsCompanyId] ?: BigInteger.ZERO
        val companyUserBalance = companyBonuses[currentUserId] ?: BigInteger.ZERO

        if (userBalance != companyUserBalance)
            return Result.Failure("Something went wrong with the balances. They should always be equal.")

        if (userBalance < sellsAmount)
            return Result.Failure("User doesn't have enough bonuses to place an exchange offer!")

        // logic - seller loses his bonuses to exchange
        userBonuses[sellsCompanyId] = userBalance - sellsAmount
        companyBonuses[currentUserId] = companyUserBalance - sellsAmount

        DummyBackend.users[currentUserId] = user.copy(bonuses = userBonuses)
        DummyBackend.companies[sellsCompanyId] = sellsCompany.copy(bonuses = companyBonuses)

        val offer = ExchangeOffer(
            id = DummyBackend.nextId(),
            ownerId = currentUserId,
            sellsCompanyId = sellsCompanyId,
            sellsAmount = sellsAmount,
            buysCompanyId = buysCompanyId,
            buysAmount = buysAmount
        )

        DummyBackend.exchangeOffers[offer.id] = offer

        return Result.Success(offer.id)
    }

    override suspend fun acceptExchangeOffer(offerId: ExchangeOfferId): Result<Unit, String> {
        // validation
        val offer = DummyBackend.exchangeOffers[offerId]
            ?: return Result.Failure("Exchange offer with id '$offerId' not found!")

        val seller = DummyBackend.users[offer.ownerId]
            ?: return Result.Failure("User with id '${offer.ownerId}' not found!")

        val buyer = DummyBackend.users[currentUserId]
            ?: return Result.Failure("User with id '$currentUserId' not found!")

        val sellsCompany = DummyBackend.companies[offer.sellsCompanyId]
            ?: return Result.Failure("Company with id '${offer.buysCompanyId}' not found!")

        val buysCompany = DummyBackend.companies[offer.buysCompanyId]
            ?: return Result.Failure("Company with id '${offer.buysCompanyId}' not found!")

        // logic
        val buyerBonuses = buyer.bonuses.toMutableMap()
        val buysCompanyBonuses = buysCompany.bonuses.toMutableMap()
        val sellerBonuses = seller.bonuses.toMutableMap()
        val sellsCompanyBonuses = sellsCompany.bonuses.toMutableMap()

        // buyer loses his bonuses to exchange
        val buyerBuyBalance = buyerBonuses[buysCompany.id!!] ?: BigInteger.ZERO
        val buysCompanyBuyerBalance = buysCompanyBonuses[buyer.id!!] ?: BigInteger.ZERO

        if (buyerBuyBalance != buysCompanyBuyerBalance)
            return Result.Failure("Something went wrong with the balances. They should always be equal.")

        if (buyerBuyBalance < offer.buysAmount)
            return Result.Failure("User doesn't have enough bonuses to place an exchange offer!")

        buyerBonuses[buysCompany.id] = buyerBuyBalance - offer.buysAmount
        buysCompanyBonuses[buyer.id] = buysCompanyBuyerBalance - offer.buysAmount

        // buyer receives bonuses from seller
        val buyerSellsBalance = buyerBonuses[sellsCompany.id!!] ?: BigInteger.ZERO
        val sellsCompanyBuyerBalance = sellsCompanyBonuses[buyer.id] ?: BigInteger.ZERO

        if (buyerSellsBalance != sellsCompanyBuyerBalance)
            return Result.Failure("Something went wrong with the balances. They should always be equal.")

        buyerBonuses[sellsCompany.id] = buyerSellsBalance + offer.sellsAmount
        sellsCompanyBonuses[buyer.id] = sellsCompanyBuyerBalance + offer.sellsAmount

        // seller receives his bonuses for exchange
        val sellerBalance = sellerBonuses[buysCompany.id] ?: BigInteger.ZERO
        val buysCompanySellerBalance = buysCompanyBonuses[seller.id!!] ?: BigInteger.ZERO

        if (sellerBalance != buysCompanySellerBalance)
            return Result.Failure("Something went wrong with the balances. They should always be equal.")

        sellerBonuses[buysCompany.id] = sellerBalance + offer.buysAmount
        buysCompanyBonuses[buyer.id] = buysCompanySellerBalance + offer.buysAmount

        // saving
        DummyBackend.users[buyer.id] = buyer.copy(bonuses = buyerBonuses)
        DummyBackend.users[seller.id] = seller.copy(bonuses = sellerBonuses)
        DummyBackend.companies[buysCompany.id] = buysCompany.copy(bonuses = buysCompanyBonuses)
        DummyBackend.companies[sellsCompany.id] = sellsCompany.copy(bonuses = sellsCompanyBonuses)

        return Result.Success(Unit)
    }

    override suspend fun removeExchangeOffer(offerId: ExchangeOfferId): Result<Unit, String> {
        // validation
        val offer = DummyBackend.exchangeOffers[offerId]
            ?: return Result.Failure("Exchange offer with id '$offerId' not found!")

        val user = DummyBackend.users[offer.ownerId]
            ?: return Result.Failure("User with id '${offer.ownerId}' not found!")

        val sellsCompany = DummyBackend.companies[offer.sellsCompanyId]
            ?: return Result.Failure("Company with id '${offer.sellsCompanyId}' not found!")

        if (user.id != currentUserId)
            return Result.Failure("User with id '$currentUserId' is not an owner of the exchange offer!")

        val userBonuses = user.bonuses.toMutableMap()
        val companyBonuses = sellsCompany.bonuses.toMutableMap()

        val userBalance = userBonuses[sellsCompany.id!!] ?: BigInteger.ZERO
        val companyUserBalance = companyBonuses[currentUserId] ?: BigInteger.ZERO

        if (userBalance != companyUserBalance)
            return Result.Failure("Something went wrong with the balances. They should always be equal.")

        // logic - seller receives his bonuses back
        userBonuses[sellsCompany.id] = userBalance + offer.sellsAmount
        companyBonuses[currentUserId] = companyUserBalance + offer.sellsAmount

        DummyBackend.users[currentUserId] = user.copy(bonuses = userBonuses)
        DummyBackend.companies[sellsCompany.id] = sellsCompany.copy(bonuses = companyBonuses)

        DummyBackend.exchangeOffers.remove(offerId)

        return Result.Success(Unit)
    }

    override suspend fun getUsers(ids: List<UserId>): Result<List<User>, String> {
        if (ids.isEmpty()) return Result.Success(DummyBackend.users.values.toList())

        return Result.Success(DummyBackend.users.values.filter { ids.contains(it.id) })
    }

    override suspend fun getCompanies(ids: List<CompanyId>): Result<List<Company>, String> {
        if (ids.isEmpty()) return Result.Success(DummyBackend.companies.values.toList())

        return Result.Success(DummyBackend.companies.values.filter { ids.contains(it.id) })
    }

    override suspend fun getRewards(ids: List<RewardId>): Result<List<Reward>, String> {
        if (ids.isEmpty()) return Result.Success(DummyBackend.rewards.values.toList())

        return Result.Success(DummyBackend.rewards.values.filter {
            when (it) {
                is Reward.Common -> ids.contains(it.id)
                is Reward.Custom -> ids.contains(it.id)
            }
        })
    }

    override suspend fun getRewardImages(ids: List<RewardImageId>): Result<List<RewardImage>, String> {
        if (ids.isEmpty()) return Result.Success(DummyBackend.rewardImages.values.toList())

        return Result.Success(DummyBackend.rewardImages.values.filter {
            when (it) {
                is RewardImage.Discount -> ids.contains(it.id)
                is RewardImage.Gift -> ids.contains(it.id)
            }
        })
    }

    override suspend fun getExchangeOffers(
        ids: List<ExchangeOfferId>
    ): Result<List<ExchangeOffer>, String> {
        if (ids.isEmpty()) return Result.Success(DummyBackend.exchangeOffers.values.toList())

        return Result.Success(DummyBackend.exchangeOffers.values.filter { ids.contains(it.id) })
    }

    override suspend fun getEvents(
        userId: UserId?,
        companyId: CompanyId?
    ): Result<List<Event>, String> {
        TODO("Not yet implemented")
    }
}

fun dummySetup() {
    var dummyAvatarBytes = Base64.decode(
        "",
        Base64.DEFAULT
    )
    var mcDoodlesLogoBytes = Base64.decode(
        "",
        Base64.DEFAULT
    )

    val user1Client: IClient = DummyClient()
    val user2Client: IClient = DummyClient()

    runBlocking {
        // registering users
        val user1 = user1Client
            .registerUser(username = "user1", password = "123", avatarBytes = dummyAvatarBytes)
            .unwrap()

        val user2 = user2Client
            .registerUser(username = "user2", password = "123", avatarBytes = dummyAvatarBytes)
            .unwrap()

        // creating companies
        val company1Id = user1Client
            .registerCompany(
                name = "McDoodles",
                logoBytes = Base64.decode(
                    "",
                    Base64.DEFAULT
                ),
                description = "the world's largest restaurant chain by revenue, serving over 69 million customers daily in over 100 countries across 37,855 outlets as of 2018",
                discount = BigInteger.ONE,
            )
            .unwrap()

        val company2Id = user2Client
            .registerCompany(
                name = "Vape Shop",
                logoBytes = Base64.decode(
                    "",
                    Base64.DEFAULT
                ),
                description = "around a third of all sales of e-cigarette products take place in vape shops",
                discount = BigInteger.ONE,
            )
            .unwrap()

        // adding rewards
        val rewardImg1 = RewardImage.Discount(
            description = "on every product",
            price = 300.toBigInteger(),
            amount = OwnedAsset.Discount.Percent(20),
            ofCompanyId = company1Id
        )
        val rewardImg1Id = user1Client
            .createRewardImage(rewardImg1)
            .unwrap()

        val rewardImg2 = RewardImage.Discount(
            description = "on your next Lucky Meal",
            price = 200.toBigInteger(),
            amount = OwnedAsset.Discount.Currency(233.rub),
            ofCompanyId = company1Id
        )
        val rewardImg2Id = user1Client
            .createRewardImage(rewardImg2)
            .unwrap()

        val rewardImg3 = RewardImage.Gift(
            description = "0.33l bottle of Koka-Cola",
            price = 75.toBigInteger(),
            ofCompanyId = company1Id
        )
        val rewardImg3Id = user1Client
            .createRewardImage(rewardImg3)
            .unwrap()

        // issuing bonuses
        user1Client.issueBonuses(company1Id, user2.id!!, 1000.toBigInteger()).unwrap()
        user1Client.issueBonuses(company1Id, user1.id!!, 499.toBigInteger()).unwrap()

        user2Client.issueBonuses(company2Id, user1.id, 2000.toBigInteger()).unwrap()

        // spending bonuses
        user1Client.transferBonuses(company1Id, user2.id, 50.toBigInteger()).unwrap()

        val user2Reward1Id = user2Client.purchaseCustomReward(rewardImg2Id).unwrap()
        val user2Reward2Id = user2Client.purchaseCustomReward(rewardImg3Id).unwrap()

        val user1Reward1Id =
            user1Client.purchaseCommonReward(company2Id, 500.toBigInteger()).unwrap()

        // using rewards
        user2Client.useReward(user2Reward1Id).unwrap()

        // placing some exchange offers
        val exchangeOffer1Id = user1Client.placeExchangeOffer(
            company2Id, 200.toBigInteger(),
            company1Id, 150.toBigInteger()
        ).unwrap()

        val exchangeOffer2Id = user2Client.placeExchangeOffer(
            company1Id, 100.toBigInteger(),
            company2Id, 90.toBigInteger()
        ).unwrap()
    }
